import{_ as d}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as n,c as l,a as c,e as i,w as p,b as a,d as t,f as r}from"./app-020ff5a3.js";const h="/assets/image-6-bcb741d2.png",u="/assets/image-4-27144ac3.png",m="/assets/image-5-3a0be068.png",f="/assets/image-1-3791bf48.png",_="/assets/image-2-89c8217e.png",A="/assets/image-daa4e091.png",g="/assets/image-3-f73de431.png",x="/assets/image-7-dc4d0c4a.png",P={},b=a("p",null,"本文主要讲解Autosar解决方案相关内容",-1),C=r('<h2 id="autosar技术标准框架" tabindex="-1"><a class="header-anchor" href="#autosar技术标准框架" aria-hidden="true">#</a> Autosar技术标准框架</h2><p>AUTOSAR技术标准针对不同的需求提供了不同的解决方案，以涵盖汽车软件开发领域的各种应用场景</p><h3 id="adaptive-platform-ap" tabindex="-1"><a class="header-anchor" href="#adaptive-platform-ap" aria-hidden="true">#</a> Adaptive Platform(AP)</h3><p>Adaptive Platform是AUTOSAR为开发安全相关的高算力ECU，如自动驾驶，而提供的解决方案</p><p>AP分层软件架构特点:</p><ul><li>支持采用C++作为应用软件的编程语言</li><li>采用面向服务的架构（SOA）</li><li>支持多任务并行处理</li><li>尽可能重用已有标准</li><li>支持功能安全和信息安全</li><li>有计划的动态性，而非完全动态</li><li>支持基于敏捷的开发过程 <img src="'+h+'" alt="Alt text" loading="lazy"></li></ul><h3 id="classic-platform-cp" tabindex="-1"><a class="header-anchor" href="#classic-platform-cp" aria-hidden="true">#</a> Classic Platform(CP)</h3><p>Classic Platform是AUTOSAR为开发硬实时、高安全性嵌入式系统，如电驱控制，而提供的解决方案</p><p>CP分层软件架构支持：</p><ul><li>对硬件进行抽象，实现软硬解耦</li><li>对Runnable和Task进行调度（操作系统）</li><li>抽象部署在同一ECU或者不同ECU上的应用之间的通信，实现整车层面的虚拟功能总线</li><li>故障诊断及诊断服务</li><li>功能安全服务</li><li>今信息安全服务 <img src="'+u+'" alt="Alt text" loading="lazy"></li></ul><p>CP所定义的系统服务、存储、通信、硬件1/0和复杂驱动等各个基础软件技术栈，其作用类似于人体的神经系统。相对应地，控制器硬件可以理解为身体，而应用软件可以理解为大脑或灵魂。 <img src="'+m+'" alt="Alt text" loading="lazy"></p><h3 id="foundation-fo" tabindex="-1"><a class="header-anchor" href="#foundation-fo" aria-hidden="true">#</a> Foundation(FO)</h3><p>Foundation包含了AP和CP之间通用的内容以保证二者之间以及二者与非AUTOSAR系统之间的兼容性</p><p>原文如下： <img src="'+f+'" alt="Alt text" loading="lazy"><img src="'+_+'" alt="Alt text" loading="lazy"></p>',14),O=r('<h4 id="fo部分所定义的通用特性" tabindex="-1"><a class="header-anchor" href="#fo部分所定义的通用特性" aria-hidden="true">#</a> FO部分所定义的通用特性</h4><p>FO部分所定义的内容为“基础设施”，同时适用于CP和AP两种系统，以保证二者之间的兼容性。如通信协议、元模型、主干需求与术语、文件模板等。</p><h3 id="ap、cp、fo间的关系" tabindex="-1"><a class="header-anchor" href="#ap、cp、fo间的关系" aria-hidden="true">#</a> AP、CP、FO间的关系</h3><figure><img src="'+A+'" alt="Alt text" tabindex="0" loading="lazy"><figcaption>Alt text</figcaption></figure><h3 id="cp-与-ap-的区别" tabindex="-1"><a class="header-anchor" href="#cp-与-ap-的区别" aria-hidden="true">#</a> CP 与 AP 的区别</h3><table><thead><tr><th>CP</th><th>AP</th></tr></thead><tbody><tr><td>Based on OSEK</td><td>Based on POSIX</td></tr><tr><td>Execution of code directly from ROM</td><td>App is loaded from persistent memory into RAM</td></tr><tr><td>Same address space for all applications (MPU support for safety)</td><td>Each application has its own (virtual) address space (MMU support)</td></tr><tr><td>Optimized for signal-based communication (CAN, FlexRay)</td><td>Service-oriented communication</td></tr><tr><td>Fixed task configuration</td><td>Support of multiple (dynamic) scheduling strategies</td></tr><tr><td>Specification</td><td>Specification and code</td></tr><tr><td>从CP到AP,实时性与安全性需求减少，算力需求增加</td><td></td></tr></tbody></table><h3 id="基于区域集中式ee架构的整车部署" tabindex="-1"><a class="header-anchor" href="#基于区域集中式ee架构的整车部署" aria-hidden="true">#</a> 基于区域集中式EE架构的整车部署</h3><p><img src="'+g+'" alt="Alt text" loading="lazy"> CP应用范围更广，AP与CP相辅相成，AP不会替代AP</p><h2 id="autosar标准制定方式" tabindex="-1"><a class="header-anchor" href="#autosar标准制定方式" aria-hidden="true">#</a> Autosar标准制定方式</h2><h3 id="autosar年度关键节点" tabindex="-1"><a class="header-anchor" href="#autosar年度关键节点" aria-hidden="true">#</a> AUTOSAR年度关键节点</h3><ul><li>每年11月份集中发布FO、CP和AP标准</li><li>AP演示代码在次年春季发布</li><li>每年上半年举办开放大会AOC</li><li>每年12月份组织标准发布会</li><li>年中组织全体工作组会议</li></ul><figure><img src="'+x+'" alt="Alt text" tabindex="0" loading="lazy"><figcaption>Alt text</figcaption></figure><h3 id="参考链接" tabindex="-1"><a class="header-anchor" href="#参考链接" aria-hidden="true">#</a> 参考链接</h3>',13),y={href:"https://www.autosar.org/fileadmin/standards/R20-11/FO/AUTOSAR_TR_FoundationReleaseOverview.pdf",target:"_blank",rel:"noopener noreferrer"},R=a("div",{class:"hint-container tip"},[a("p",{class:"hint-container-title"},"提示"),a("p",null,"若本文对您有用，欢迎送个表情包或评论 ;若有不对之处或建议，欢迎评论")],-1);function S(v,F){const o=e("center"),s=e("ExternalLinkIcon");return n(),l("div",null,[b,c(" more "),C,i(o,null,{default:p(()=>[t("FO角色")]),_:1}),O,a("p",null,[a("a",y,[t("AUTOSAR_TR_FoundationReleaseOverview"),i(s)]),t(" AUTOSAR中国官方培训课程")]),R])}const E=d(P,[["render",S],["__file","autosar_solution_summary.html.vue"]]);export{E as default};
